package main

import (
	"fmt"
)

func createSubstr(num string, grouplen int) []string {
	result := make([]string, 0)
	n := len(num)
	start := 0
	for start < n {
		result = append(result, num[start:start+grouplen])
		start = start + grouplen
	}
	return result
}

func addCommas(str string) string {
	result := ""
	count := 0
	for i := len(str) - 1; i >= 0; i-- {
		if count > 0 && count%3 == 0 {
			result = "," + result
		}
		count += 1
		result = string(str[i]) + result
	}
	result = "$" + result
	return result
}

func main() {
	num := ""
	result := createSubstr(num, 16)
	for _, res := range result {
		fmt.Println(addCommas(res))
	}
}
